                var newStatus;
                //Trigger the close alert or re-open alert flow
                if (alert.status === 'Open') {
                    promiseResults = ctrl.alertFeedback.handleCloseAlertPopupFlow(alert);
                    newStatus = 'closed';
                }
                else {
                    promiseResults = ctrl.alertFeedback.handleOpenAlertPopupFlow(alert);
                    newStatus = 'open';
                }
